insert  into `eriko_shop_goods`(`gid`,`sid`,`gname`,`mprice`,`price`,`poster`,`pictures`,`issale`,`ctime`,`etime`,`stock`,`sales`,`detail`,`subtitle`,`endtime`) values (4,1,'抢购商品1','18.00','12.00','/Uploads/images/20161125/5837dde281dc5.jpg','/Uploads/images/20161125/5837ddf6dbc3f.jpg,/Uploads/images/20161125/5837ddf6e6437.jpg',0,1480056337,1481852698,11,0,'&lt;p&gt;苹果苹果&lt;/p&gt;','抢枪抢',1481939071),(5,1,'大苹果','100.00','12.00','/Uploads/images/20161208/5849259ea4711.png','/Uploads/images/20161208/584925bd37e45.png',0,1481188801,0,90,10,'&lt;p&gt;刚上的货 特别好吃的水果 大家都来买吧&lt;/p&gt;','走过路过不要错过',1482249600),(6,1,'老曹','5.00','90.00','/Uploads/images/20161208/5849260ea2977.png','/Uploads/images/20161208/584926158aee1.png',0,1481188915,1481853270,10,0,'&lt;p&gt;太好了啊&lt;/p&gt;','好人',1481966520),(7,2,'极品羊蝎子','85.00','0.01','/Uploads/images/20161211/584cdd4050599.jpg','/Uploads/images/20161211/584cdccb75188.jpg',1,1481432302,1481853321,14,9,'&lt;p&gt;&lt;span style=&quot;font-weight: 700;&quot;&gt;羊蝎子&lt;/span&gt;就是带里脊肉和脊髓的完整的羊脊椎骨，因其形跟&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/43519/7129095.htm&quot; data-lemmaid=&quot;25470&quot; style=&quot;color: rgb(19, 110, 194); text-decoration: none;&quot;&gt;蝎子&lt;/a&gt;相似，故而俗称羊蝎子。羊蝎子低脂肪、低胆固醇、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/117029.htm&quot; style=&quot;color: rgb(19, 110, 194); text-decoration: none;&quot;&gt;高蛋白&lt;/a&gt;，富含钙质。易于吸收，有滋阴补肾，美颜壮阳功效。常可用来做&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/173065.htm&quot; style=&quot;color: rgb(19, 110, 194); text-decoration: none;&quot;&gt;清汤火锅&lt;/a&gt;，味道鲜美。&lt;/p&gt;&lt;p&gt;羊蝎子有“补钙之王”的美誉。羊蝎子经过长时间的焖，有利于促进钙的吸收，达到补钙的功效。故老年食之能缓解骨质疏松；中年食之，能养颜美容；少年食之，能健脑增高，并对患有&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/44334.htm&quot; style=&quot;color: rgb(19, 110, 194); text-decoration: none;&quot;&gt;慢性结肠炎&lt;/a&gt;、胃炎、气管炎等症状有明显的疗效。对工作、学习疲劳困倦，可起到加油充电的作用，堪称老少皆宜，四季均享的上乘&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/758097.htm&quot; style=&quot;color: rgb(19, 110, 194); text-decoration: none;&quot;&gt;美味佳肴&lt;/a&gt;。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Uploads/ueditor/2016-12-11/1481432300171972.jpg&quot; title=&quot;1481432300171972.jpg&quot; alt=&quot;羊蝎子.jpg&quot;/&gt;&lt;/p&gt;','大促销',1481698970),(8,1,'测试','115.00','111.00','/Uploads/images/20161216/58534a89e0c18.jpg','/Uploads/images/20161216/58534a9cc53a2.jpg',0,1481853631,1481853719,1,0,'&lt;p&gt;测试&lt;br/&gt;&lt;/p&gt;','测试子标题',1481939984);

