/*Table structure for table `eriko_shop_order` */

DROP TABLE IF EXISTS `eriko_shop_order`;

CREATE TABLE `eriko_shop_order` (
  `orderid` varchar(50) NOT NULL COMMENT '订单号',
  `uid` int(11) NOT NULL COMMENT '用户ID',
  `gid` int(11) NOT NULL COMMENT '商品ID',
  `nums` int(11) NOT NULL DEFAULT '0' COMMENT '数量',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '单价',
  `totalamount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '总金额',
  `payamount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '支付金额',
  `gname` varchar(255) NOT NULL COMMENT '商品名称',
  `subtitle` varchar(255) NOT NULL COMMENT '子标题',
  `poster` varchar(128) NOT NULL COMMENT '封面',
  `ctime` int(11) DEFAULT NULL COMMENT '下单时间',
  `ordertype` int(11) NOT NULL COMMENT '订单类型1抢购2夺宝',
  `payway` int(11) NOT NULL COMMENT '1支付宝2微信',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '状态0待支付1已付款2待收货3已收货',
  `isbonus` int(11) NOT NULL DEFAULT '0' COMMENT '是否中奖0待开奖1中奖2未中奖',
  `isfilladdress` int(11) NOT NULL DEFAULT '0' COMMENT '是否填写地址0否1是',
  `paytime` int(11) DEFAULT NULL COMMENT '支付时间',
  `rname` varchar(50) DEFAULT NULL COMMENT '收货人',
  `mobile` varchar(50) DEFAULT NULL COMMENT '联系电话',
  `area` varchar(128) DEFAULT NULL COMMENT '收货区域',
  `address` varchar(512) DEFAULT NULL COMMENT '收货地址',
  `outorderid` varchar(50) DEFAULT NULL,
  `tradeid` varchar(50) DEFAULT NULL,
  `codes` varchar(1024) DEFAULT NULL COMMENT '号码',
  `canceltime` int(11) DEFAULT NULL COMMENT '取消时间',
  `remaindtime` int(11) DEFAULT NULL COMMENT '倒计时剩余时间',
  `receivetime` int(11) DEFAULT NULL COMMENT '收货时间',
  `expressno` varchar(50) DEFAULT NULL,
  `expressway` int(11) DEFAULT NULL,
  `mname` varchar(50) DEFAULT NULL,
  `handletime` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`orderid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单';

