/*Table structure for table `eriko_user` */

DROP TABLE IF EXISTS `eriko_user`;

CREATE TABLE `eriko_user` (
  `uid` int(11) NOT NULL AUTO_INCREMENT,
  `phone` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '用户名',
  `ucode` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL,
  `password` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '密码',
  `username` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '姓名',
  `idcard` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '身份证号',
  `company` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '公司',
  `department` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '部门',
  `job` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '职务',
  `employee_num` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '工位号',
  `car_num` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '车证编号',
  `place_id` int(11) DEFAULT NULL COMMENT '区域id',
  `nickname` varchar(124) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '昵称',
  `gender` tinyint(1) DEFAULT '0' COMMENT '性别 1男2女',
  `avator` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '头像',
  `last_login_time` int(10) DEFAULT NULL COMMENT '最后登录时间',
  `ctime` int(11) DEFAULT NULL COMMENT '注册时间',
  `etime` int(11) unsigned DEFAULT NULL,
  `openid` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '微信id',
  `authstatus` int(11) unsigned DEFAULT '0' COMMENT '0-审核中1-已认证2-审核驳回',
  `status` int(1) DEFAULT '0' COMMENT '是否锁定 1锁定0正常',
  `registration_id` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '极光id',
  `is_limit` int(1) DEFAULT '0' COMMENT '是否是外围用户 0否1是',
  PRIMARY KEY (`uid`),
  UNIQUE KEY `phone` (`phone`)
) ENGINE=InnoDB AUTO_INCREMENT=503 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='用户表';

