/*!50003 DROP FUNCTION IF EXISTS `cal_xy_distance` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`lawyer`@`%` FUNCTION `cal_xy_distance`(`x0` FLOAT , `y0` FLOAT, `x1` FLOAT, `y1` FLOAT) RETURNS float
    DETERMINISTIC
BEGIN
       DECLARE radLat1 FLOAT DEFAULT 0.0;
       DECLARE radLat2 FLOAT DEFAULT 0.0;
       DECLARE a FLOAT DEFAULT 0.0;
       DECLARE b FLOAT DEFAULT 0.0;
       DECLARE s FLOAT DEFAULT 0.0;
       SET radLat1 = x0*PI()/180.0;
       SET radLat2 = x1*PI()/180.0;
       SET a = radLat1-radLat2;
       SET b = (y0-y1)*PI()/180.0;
       SET s=2 * ASIN(SQRT(POW(SIN(a/2),2) +COS(radLat1)*COS(radLat2)*POW(SIN(b/2),2)));
       SET s = s*6378.137;
       SET s = ROUND(s * 10000) / 10000;
       RETURN s;
    END */$$
DELIMITER ;

